insert into `odd_ingredients` values (1037,'Rose de Mai Cocktail','3 cl Pineapple juice \r\n1-2 tsp Raspberry syrup \r\n6-7 cl Cream','Shake very well pineapple juice, raspberry fruit-syrup and cream with ice cubes. Strain into a large cocktail glass.','Cocktail','Cocktail glass','Non alcoholic','Pineapple juice,Raspberry syrup,Cream');
insert into `odd_ingredients` values (1038,'St. Clement\'s','1 part fresh Orange juice \r\n1 part Bitter lemon','Mix with ice, serve. \"Oranges and lemons say the bells of St. Clement\'s\"','Cocktail','Unknown glass type','Non alcoholic','Orange juice,Bitter lemon');
insert into `odd_ingredients` values (1039,'Rhubarb Syrup','1 lb Rhubarb \r\n1 cup Water \r\n2 cups Sugar \r\n2 tblsp Rosewater','Remove strings from rhubarb, wash, and cut into 1/2\" pieces. Wrap rhubarb in cheesecloth and tie shut. Bring sugar and water to boil. Put cheesecloth in the syrup and simmer over medium heat for 30 minutes or until it thickens. Remove cheesecloth, squeeze it to get the juice out. Pour syrup into a clean, dry bottle and cork tightly. To mix add 1 part syrup to 3 parts water and add 2 ice cubes per person. Serve well chilled.','Cocktail','Unknown glass type','Non alcoholic','Rhubarb,Water,Sugar,Rosewater');
insert into `odd_ingredients` values (1040,'Shirley Temple','20 cl Ginger ale \r\n3 cl Grenadine syrup','Build in a highball glass. Add ginger ale over ice and sprinkle grenadine syrup over it. Garnish with a lemon slice and a cherry.','Cocktail','Highball glass','Non alcoholic','Ginger ale,Grenadine');
insert into `odd_ingredients` values (1041,'Tomato Cocktail','1/2 tsp Worcestershire sauce \r\n1 tsp Lemon juice \r\n10 cl Tomato juice \r\nSalt \r\nBlack pepper','Stir in a mixing glass Worcestershire sauce, lemon juice, tomato juice, and a little salt and pepper over ice cubes. Strain off into a large cocktail glass.','Cocktail','Cocktail glass','Non alcoholic','Worcestershire sauce,Lemon juice,Tomato juice,Salt,Black pepper');
