insert into `odd_ingredients` values (1251,'Margarita','1 1/2 oz Tequila \r\n1/2 oz Triple sec \r\n1 oz Lime juice \r\nSalt','Rub rim of cocktail glass with lime juice, dip rim in salt. Shake all ingredients with ice, strain into the salt-rimmed glass, and serve.','Ordinary Drink','Cocktail glass','Alcoholic','Tequila,Triple sec,Lime juice,Salt');
insert into `odd_ingredients` values (1252,'Polarbear #1','1 part Vodka \r\n1 part Lime juice \r\n2 parts 7-Up (or sprite)','Take one part vodka into the glass, pour one part lime in the same glass. Then you take two parts of 7-up or sprite. Mix this together with a straw or something like that. Ice is recommended.','Ordinary Drink','Unknown glass type','Alcoholic','Vodka,Lime juice,7-Up');
